SELECT (SELECT FULLNAME FROM ORGANIZATION_ O WHERE O.ORGANIZATIONID=T.ORGANIZATIONID) AS organizationFullName, USERID , CREATEDATE,MODIFIEDDATE,DEFAULTUSER,PASSWORD,PASSWORDMODIFIEDDATE, REMINDERQUERYQUESTION,REMINDERQUERYANSWER , SCREENNAME,EMAILADDRESS ,GREETING,USERNAME,JOBTITLE,LOGINDATE,LOGINIP,LASTLOGINDATE,LASTLOGINIP,STATUS, EMPLOYEENUMBER,MALE,BIRTHDAY,TEL,DESCRIPTION,ORGANIZATIONID,USERTYPE from (SELECT ID,USERID,USERACCOUNT,METHODS,OPERTIME,USERIP,CLASSNAME,EXCETPTION,DESCRIPTION,EXETIME,arguments,RETURNOBJECT,SUCESS,ROWNUM RN FROM LOGEVENT w
(OPERTIME >=to_date(#{STARTTIME},'yyyy-mm-dd hh24:mi:ss'))
(OPERTIME <=to_date(#{ENDTIME},'yyyy-mm-dd hh24:mi:ss'))
(USERID =#{USERID})
and ROWNUM <= #{endRow}
)T
and RN >= #{startRow}
